mapper.update:
update bill
money = #{money}, title = #{title}, typeid = #{typeid}, remark = #{remark}, paywayid = #{paywayid},
where id = #{id}

mapper.select:
select *, type.name type from bill left join type on bill.typeid = type.id left join payway on paywayid = payway.id left join user on bill.userid = user.id

    and bill.id = #{id}
     and userid in (select id from user where houseid = #{houseid})
     and typeid = #{type}
     and userid = #{userid}
     and title = #{title}
     and realname = #{realname}
     and paywayid = #{paywayid}
     and time >= #{startTime}
     and time <= #{endTime}

select bill.* , user.realname, payway.payway, type.name type from bill left join user on bill.userid = user.id left join type on bill.typeid = type.id
left join payway on paywayid = payway.id

     and userid in (select id from user where houseid = #{data.houseid})
     and bill.id = #{data.id}
     and bill.title = #{data.title}
     and bill.typeid = #{data.type}
     and bill.userid = #{data.userid}
     and realname like concat('%',#{data.realname},'%')
     and money = #{data.money}
     and remark like concat('%',#{data.remark},'%')
     and paywayid = #{data.paywayid}
     and time >= #{data.startTime}
     and time <= #{data.endTime}

order by time desc
limit #{beginIndex},#{pageSize}

select count(bill.id) from bill left join user on bill.userid = user.id left join type on bill.typeid = type.id
left join payway on paywayid = payway.id

     and userid in (select id from user where houseid = #{data.houseid})
     and bill.id = #{data.id}
     and bill.title = #{data.title}
     and bill.typeid = #{data.type}
     and bill.userid = #{data.userid}
     and realname like concat('%',#{data.realname},'%')
     and money = #{data.money}
     and remark like concat('%',#{data.remark},'%')
     and paywayid = #{data.paywayid}
     and time >= #{data.startTime}
     and time <= #{data.endTime}
