mapper.update:
update moneymanage
money = #{money}, name = #{name}, rate = #{rate},
where id = #{id}

mapper.select:
select m.* from moneymanage m left join user on m.userid = user.id

     and userid in (select id from user where houseid = #{houseid})
     and userid = #{userid}
     and name = #{name}
     and realname = #{realname}

select m.* , user.realname from moneymanage m left join user on m.userid = user.id

     and userid in (select id from user where houseid = #{data.houseid})
     and m.userid = #{data.userid}
     and realname like concat('%',#{data.realname},'%')
     and m.name like concat('%',#{data.name},'%')

limit #{beginIndex},#{pageSize}
